import React, { useState, useEffect } from "react";
import "./EmployerDashboard.css";

const EmployerDashboard = () => {
  const [interviews, setInterviews] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newInterview, setNewInterview] = useState({
    candidate: "",
    position: "",
    date: "",
    time: "",
    interviewMode: "Online",
    location: "",
    interviewType: "Technical Interview",
    panelMembers: "",
    meetingLink: "",
    instructions: "",
  });

  useEffect(() => {
    // For now, we'll mock the data
    const mockInterviews = [
      {
        id: 1,
        candidate: "John Doe",
        position: "Software Engineer",
        date: "2024-12-15 10:00 AM",
        status: "Scheduled",
      },
      {
        id: 2,
        candidate: "Jane Smith",
        position: "Product Manager",
        date: "2024-12-16 02:00 PM",
        status: "Scheduled",
      },
    ];
    setInterviews(mockInterviews);
  }, []);

  const handleReschedule = (id) => {
    alert(`Reschedule interview for ID: ${id}`);
    // Add logic for rescheduling
  };

  const handleCancel = (id) => {
    alert(`Cancel interview for ID: ${id}`);
    // Add logic for canceling
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewInterview((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add new interview to the interviews list (you'll typically send this to a backend API)
    setInterviews((prevInterviews) => [
      ...prevInterviews,
      { ...newInterview, id: prevInterviews.length + 1, status: "Scheduled" },
    ]);
    setShowForm(false); // Close the form after submission
  };

  return (
    <div className="employer-dashboard">
      <div className="employer-header">Employer Dashboard</div>

      <button
        className="add-interview-btn"
        onClick={() => setShowForm(!showForm)}
      >
        {showForm ? "Cancel" : "Add New Interview"}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="interview-form">
          <div className="form-group">
            <label>Candidate Name:</label>
            <input
              type="text"
              name="candidate"
              value={newInterview.candidate}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={newInterview.position}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Interview Mode:</label>
            <select
              name="interviewMode"
              value={newInterview.interviewMode}
              onChange={handleInputChange}
            >
              <option value="Online">Online</option>
              <option value="Offline">Offline</option>
            </select>
          </div>

          {newInterview.interviewMode === "Offline" && (
            <div className="form-group">
              <label>Location:</label>
              <input
                type="text"
                name="location"
                value={newInterview.location}
                onChange={handleInputChange}
              />
            </div>
          )}

          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              name="date"
              value={newInterview.date}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time:</label>
            <input
              type="time"
              name="time"
              value={newInterview.time}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Type of Interview:</label>
            <select
              name="interviewType"
              value={newInterview.interviewType}
              onChange={handleInputChange}
            >
              <option value="Technical Interview">Technical Interview</option>
              <option value="HR Interview">HR Interview</option>
              <option value="Managerial Round">Managerial Round</option>
              <option value="Final Round">Final Round</option>
            </select>
          </div>

          <div className="form-group">
            <label>Panel Members:</label>
            <input
              type="text"
              name="panelMembers"
              value={newInterview.panelMembers}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-group">
            <label>Meeting Link (if Online):</label>
            <input
              type="text"
              name="meetingLink"
              value={newInterview.meetingLink}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-group">
            <label>Instructions for Candidate:</label>
            <textarea
              name="instructions"
              value={newInterview.instructions}
              onChange={handleInputChange}
            />
          </div>

          <button type="submit" className="submit-btn">
            Schedule Interview
          </button>
        </form>
      )}

      <div className="scheduled-interviews">
        <h3>Scheduled Interviews</h3>
        <table>
          <thead>
            <tr>
              <th>Candidate</th>
              <th>Position</th>
              <th>Interview Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {interviews.map((interview) => (
              <tr key={interview.id}>
                <td>{interview.candidate}</td>
                <td>{interview.position}</td>
                <td>{interview.date}</td>
                <td>{interview.status}</td>
                <td>
                  <button onClick={() => handleReschedule(interview.id)}>
                    Reschedule
                  </button>
                  <button onClick={() => handleCancel(interview.id)}>
                    Cancel
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmployerDashboard;
