import axios from "axios";

// Base URL for the backend API
const API_URL = "http://localhost:8080/api/interviews";

/**
 * Fetch all interviews from the backend.
 * @returns {Promise} A promise containing the list of interviews.
 */
export const getInterviews = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data; // Returns the data array
  } catch (error) {
    console.error("Error fetching interviews:", error);
    throw error;
  }
};

/**
 * Add a new interview to the backend.
 * @param {Object} interview - The interview object to add.
 * @returns {Promise} A promise containing the added interview data.
 */
export const addInterview = async (interview) => {
  try {
    const response = await axios.post(API_URL, interview);
    return response.data; // Returns the added interview data
  } catch (error) {
    console.error("Error adding interview:", error);
    throw error;
  }
};

/**
 * Delete an interview by ID from the backend.
 * @param {number} id - The ID of the interview to delete.
 * @returns {Promise} A promise containing the response from the backend.
 */
export const deleteInterview = async (id) => {
  try {
    const response = await axios.delete(`${API_URL}/${id}`);
    return response.data; // Returns the deletion response data
  } catch (error) {
    console.error("Error deleting interview:", error);
    throw error;
  }
};
