import React, { useState } from "react";
import { createInterview } from "../../services/interviewService";
import "./CreateInterviewForm.css";

const CreateInterviewForm = ({ jobApplicationId, onInterviewAdded }) => {
  const [newInterview, setNewInterview] = useState({
    date: "",
    time: "",
    location: "",
    meetingLink: "",
    panelMembers: "",
    interviewMode: "Online",
    interviewType: "Technical Interview",
    instructions: "",
    status: "Scheduled",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewInterview((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const createdInterview = await createInterview(
        newInterview,
        jobApplicationId
      );
      alert("Interview created successfully!");
      onInterviewAdded(createdInterview); // Notify parent component
    } catch (error) {
      console.error("Error creating interview:", error);
      alert("Failed to create interview.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="interview-form">
      {[
        { label: "Date", name: "date", type: "date" },
        { label: "Time", name: "time", type: "time" },
        { label: "Location", name: "location", type: "text" },
        { label: "Meeting Link", name: "meetingLink", type: "text" },
        { label: "Panel Members", name: "panelMembers", type: "text" },
      ].map((field) => (
        <div className="form-group" key={field.name}>
          <label>{field.label}:</label>
          <input
            type={field.type}
            name={field.name}
            value={newInterview[field.name]}
            onChange={handleInputChange}
            required
          />
        </div>
      ))}

      <div className="form-group">
        <label>Interview Mode:</label>
        <select
          name="interviewMode"
          value={newInterview.interviewMode}
          onChange={handleInputChange}
        >
          <option value="Online">Online</option>
          <option value="Offline">Offline</option>
        </select>
      </div>

      <div className="form-group">
        <label>Interview Type:</label>
        <select
          name="interviewType"
          value={newInterview.interviewType}
          onChange={handleInputChange}
        >
          <option value="Technical Interview">Technical Interview</option>
          <option value="HR Interview">HR Interview</option>
          <option value="Managerial Interview">Managerial Interview</option>
        </select>
      </div>

      <div className="form-group">
        <label>Instructions:</label>
        <textarea
          name="instructions"
          value={newInterview.instructions}
          onChange={handleInputChange}
        ></textarea>
      </div>

      <button type="submit" className="submit-btn">
        Create Interview
      </button>
    </form>
  );
};

export default CreateInterviewForm;
