import React, { useState, useEffect } from "react";
import {
  getInterviews,
  addInterview,
  updateInterview,
  deleteInterview,
} from "../../services/interviewService";
import "./EmployerDashboard.css";

const EmployerDashboard = () => {
  const [interviews, setInterviews] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newInterview, setNewInterview] = useState({
    candidate: "",
    position: "",
    date: "",
    time: "",
    interviewMode: "Online",
    location: "",
    interviewType: "Technical Interview",
    panelMembers: "",
    meetingLink: "",
    instructions: "",
    status: "Scheduled",
  });
  const [selectedInterview, setSelectedInterview] = useState(null);

  useEffect(() => {
    const fetchInterviews = async () => {
      try {
        const data = await getInterviews();
        setInterviews(data);
      } catch (error) {
        console.error("Error fetching interviews:", error);
      }
    };
    fetchInterviews();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewInterview((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const createdInterview = await addInterview(newInterview);
      setInterviews((prevInterviews) => [...prevInterviews, createdInterview]);
      setShowForm(false);
      setNewInterview({
        candidate: "",
        position: "",
        date: "",
        time: "",
        interviewMode: "Online",
        location: "",
        interviewType: "Technical Interview",
        panelMembers: "",
        meetingLink: "",
        instructions: "",
        status: "Scheduled",
      });
    } catch (error) {
      console.error("Error adding interview:", error);
    }
  };

  const handleViewDetails = (id) => {
    const interview = interviews.find((int) => int.id === id);
    setSelectedInterview(interview);
  };

  const handleReschedule = async (id) => {
    const interviewToReschedule = interviews.find((int) => int.id === id);
    const newDate = prompt(
      "Enter new date (YYYY-MM-DD):",
      interviewToReschedule.date
    );
    const newTime = prompt(
      "Enter new time (HH:MM):",
      interviewToReschedule.time
    );

    if (newDate && newTime) {
      try {
        const updatedInterview = {
          ...interviewToReschedule,
          date: newDate,
          time: newTime,
        };
        await updateInterview(id, updatedInterview);
        setInterviews((prev) =>
          prev.map((int) => (int.id === id ? updatedInterview : int))
        );
        alert("Interview rescheduled successfully!");
      } catch (error) {
        console.error("Error rescheduling interview:", error);
      }
    }
  };

  const handleCancel = async (id) => {
    if (window.confirm("Are you sure you want to cancel this interview?")) {
      try {
        await deleteInterview(id);
        setInterviews((prev) => prev.filter((int) => int.id !== id));
        alert("Interview canceled successfully!");
      } catch (error) {
        console.error("Error canceling interview:", error);
      }
    }
  };

  const closeModal = () => {
    setSelectedInterview(null);
  };

  return (
    <div className="employer-dashboard">
      <div className="employer-header">Employer Dashboard</div>

      <button
        className="add-interview-btn"
        onClick={() => setShowForm(!showForm)}
      >
        {showForm ? "Cancel" : "Add New Interview"}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="interview-form">
          <div className="form-group">
            <label>Candidate Name:</label>
            <input
              type="text"
              name="candidate"
              value={newInterview.candidate}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={newInterview.position}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              name="date"
              value={newInterview.date}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Time:</label>
            <input
              type="time"
              name="time"
              value={newInterview.time}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Interview Mode:</label>
            <select
              name="interviewMode"
              value={newInterview.interviewMode}
              onChange={handleInputChange}
            >
              <option value="Online">Online</option>
              <option value="Offline">Offline</option>
            </select>
          </div>
          <div className="form-group">
            <label>Location:</label>
            <input
              type="text"
              name="location"
              value={newInterview.location}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Interview Type:</label>
            <select
              name="interviewType"
              value={newInterview.interviewType}
              onChange={handleInputChange}
            >
              <option value="Technical Interview">Technical Interview</option>
              <option value="HR Interview">HR Interview</option>
              <option value="Managerial Interview">Managerial Interview</option>
            </select>
          </div>
          <div className="form-group">
            <label>Panel Members:</label>
            <input
              type="text"
              name="panelMembers"
              value={newInterview.panelMembers}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Meeting Link:</label>
            <input
              type="text"
              name="meetingLink"
              value={newInterview.meetingLink}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Instructions:</label>
            <textarea
              name="instructions"
              value={newInterview.instructions}
              onChange={handleInputChange}
            ></textarea>
          </div>
          <button type="submit" className="submit-btn">
            Add Interview
          </button>
        </form>
      )}

      {/* Scheduled interviews section */}
      <div className="scheduled-interviews">
        <h3>Scheduled Interviews</h3>
        <table>
          <thead>
            <tr>
              <th>Candidate</th>
              <th>Position</th>
              <th>Interview Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {interviews.map((interview) => (
              <tr key={interview.id}>
                <td>{interview.candidate}</td>
                <td>{interview.position}</td>
                <td>{interview.date}</td>
                <td>{interview.status}</td>
                <td>
                  <button
                    className="action-button view-button"
                    onClick={() => handleViewDetails(interview.id)}
                  >
                    View
                  </button>
                  <button
                    className="action-button reschedule-button"
                    onClick={() => handleReschedule(interview.id)}
                  >
                    Reschedule
                  </button>
                  <button
                    className="action-button cancel-button"
                    onClick={() => handleCancel(interview.id)}
                  >
                    Cancel
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal for viewing details */}
      {selectedInterview && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h2>Interview Details</h2>
            <p>
              <strong>Candidate:</strong> {selectedInterview.candidate}
            </p>
            <p>
              <strong>Position:</strong> {selectedInterview.position}
            </p>
            {/* Add more details */}
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployerDashboard;
