import React, { useState, useEffect } from "react";
import InterviewService from "../../services/interviewService";
import JobApplicationService from "../../services/jobApplicationService";
import "./EmployerDashboard.css";

const EmployerDashboard = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [selectedApplicationId, setSelectedApplicationId] = useState(null);
  const [interviews, setInterviews] = useState([]);

  useEffect(() => {
    const loadJobApplications = async () => {
      const applications = await fetchJobApplications();
      setJobApplications(applications);
    };
    loadJobApplications();
  }, []);

  const handleInterviewAdded = (newInterview) => {
    setInterviews((prev) => [...prev, newInterview]);
  };

  return (
    <div className="dashboard">
      <h1>Employer Dashboard</h1>
      <div className="applications-list">
        <h2>Job Applications</h2>
        <ul>
          {jobApplications.map((app) => (
            <li key={app.id}>
              {app.candidateName} - {app.jobTitle}
              <button onClick={() => setSelectedApplicationId(app.id)}>
                Schedule Interview
              </button>
            </li>
          ))}
        </ul>
      </div>

      {selectedApplicationId && (
        <div className="create-interview">
          <h2>Create Interview</h2>
          <CreateInterviewForm
            jobApplicationId={selectedApplicationId}
            onInterviewAdded={handleInterviewAdded}
          />
        </div>
      )}

      <div className="interviews-list">
        <h2>Interviews</h2>
        <ul>
          {interviews.map((interview) => (
            <li key={interview.id}>
              {interview.date} - {interview.time} ({interview.interviewType})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EmployerDashboard;
